import React from 'react';
import './TeacherProfile.css';
import './Courses.css';

const TeacherProfile = () => {
  const courses = [
    {
      id: 1,
      code: 'Arabic 101',
      name: 'Introduction to Arabic'
    },
    {
      id: 2,
      code: 'QU 1101',
      name: 'Introduction to Quran'  
    },
    {
      id: 3,
      code: 'Arabic 1050',
      name: 'Arabic Grammar'
    }
  ];

  // YouTube embed URL for the video placeholder
  const youtubeUrl = "https://www.youtube.com/embed/XC62pWvw4b0";

  // Open the YouTube video in a new tab
  const openVideo = () => {
    window.open(youtubeUrl, '_blank', 'noopener,noreferrer');
  };

  return (
    <div className="container mt-5">
      <div className="card p-4">
        <div className="row">
          <div className="col-md-4 text-center">
            <div className="teacher-photo mx-auto mb-3">
              <img 
                src="/arabicteacher.jpg" 
                alt="Teacher" 
                className="img-fluid rounded-circle"
                style={{ width: '200px', height: '200px', objectFit: 'cover' }}
              />
            </div>
          </div>
          <div className="col-md-8">
            <h2>Abdullah Jassim</h2>
            <h5>Arabic Professor</h5>
            <p>
              Abdullah Jassim is a dedicated Arabic teacher who has a passion for making complex concepts 
              easy to understand. He specializes in teaching Arabic, grammar, and Qurâ€™an, 
              and aims to inspire students to love learning Arabic.
            </p>
            <a href="/booking" className="btn btn-primary">Book a Session</a>
          </div>
        </div>
      </div>

      {/* Video placeholder cube */}
      <div 
        className="video-placeholder my-4"
        onClick={openVideo}
        role="button"
        tabIndex={0}
        onKeyPress={(e) => { if (e.key === 'Enter') openVideo(); }}
        aria-label="Play introductory video"
      >
        <i className="play-icon bi bi-play-circle-fill"></i>
      </div>

      <div className="courses-section">
        <h3 className="courses-title">Courses</h3>
        <div className="courses-container">
          {courses.map(course => (
            <div key={course.id} className="course-card">
              <div className="card p-3 h-100">
                <h5 className="course-code">{course.code}</h5>
                <p className="course-name mb-0">{course.name}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TeacherProfile;
